def calculate_sum(a, b):
    return a + b

def main():
    result = calculate_sum(5, 3)
    print(f"Sum is: {result}")

if __name__ == "__main__":
    main()

class Calculator:
    def __init__(self):
        self.value = 0

    def add(self, x):
        self.value += x
        return self.value

if __name__ == "__main__":
    calc = Calculator()
    print(calc.add(10))
